try{
function CheckCmdHere() { "${checkCmdString}" }
$SCSIID = "0"
$vmUuid = "${vmUuid}"
$uuid2vmObj = Get-VM -Server $viserver | ? { $_.extensiondata.config.uuid -eq "$vmUuid"} 
  if ($uuid2vmObj) { CheckCmdHere }
}catch {
      echo "Error occured: $_"
}
