/// exectue javascript content @Daway
try {
if(!isnull("Scriptlet")){eval(Scriptlet);}else{
//Begin :  your Scriptlet
//------------------------------------------------------------------------
putResult();
if(0 == num(getdid("returnCode"))){
//Begin :  if ( 0 = num(getdid("returnCode")))
	println("Sucess");
	showInfo();
	errorCode000();
//End   :  if ( 0 = num(getdid("returnCode")))
}else{
// Else Begin: if ( 0 = num(getdid("returnCode")))
	println("Faliure");

	//
	printline("=",80);
	//check Connect
	if ( checkConnect() ){
	// Begin: Check Connect
    	put("connectError","true");
		if(doRetry()){
    		putErrorInfo();

    		showInfo();
    		errorCode200();
    		//did(false);
   			did("retry");
 	}else{
    	//putErrorInfo();
    	errorCode200();
 		did(false);
    }
    put("connectError","true");
// End: Check Connect
}else{
// Begin: PowerCLI Runtime error
	errorCodeIs();
// End: PowerCLI Runtime error
}
printline("=",80);
// Else   End: if ( 0 = num(getdid("returnCode")))
}

//------------------------------------------------------------------------
//End   :  Your Scriptlet
}
// finalize oo javascirpt flow
} catch(e if e instanceof SyntaxError){
    showSyntaxError();
    did(false);
} catch (e){
    var errorMsg = str(e);
    showErrorInfo(errorMsg);
    did(false);
}finally { 
    println();
}
