// Show PowerCLI Info
function showInfo(){
    printline("-",80);
	println("returnCode : "+getdid("returnCode"));
	println("exception : "+getdid("exception"));
	println("objectsCount : "+getdid("objectsCount"));
	printline("-",80);
	println("returnResult : "+getdid("returnResult"));
	printline("=",80);
}
// Set PowerCLI Info
function putResult(){
	put("outReturnCode",getdid("returnCode"));
	put("outException",getdid("exception"));
	put("outObjectsCount",getdid("objectsCount"));
	put("outReturnResult",getdid("returnResult"));;
}
//do retry.   
// doRetry() 
function doRetry(){
// Begin: doRetry
    var myRetry = getvar(get("myRetry"));
    if ( ""==myRetry ){
      put("myRetry",getvar("retry") );
      myRetry = num(getvar("retry"));
    }else{
      myRetry = num(get("myRetry")) ;
    }
    myRetry -= 1;
    put("myRetry",myRetry);
    if(0 >= myRetry){
         return false;
    }else{
         sleep(interval);
         return true;
    }     
// End: doRetry
}
